from pybricks.hubs import PrimeHub
from pybricks.parameters import Button, Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(motor, motor_2, 56, 130)
motor_22 = Motor(Port.F, Direction.COUNTERCLOCKWISE)


# The main program starts here.
drive_base.straight(230)
drive_base.straight(-300)
while not any(prime_hub.buttons.pressed()):
    wait(1)
drive_base.straight(320)
drive_base.straight(-330)
while not any(prime_hub.buttons.pressed()):
    wait(1)
drive_base.straight(400)
drive_base.straight(-400)
while not any(prime_hub.buttons.pressed()):
    wait(1)
drive_base.straight(25)
drive_base.straight(-45)
