from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(motor, motor_2, 56, 130)
motor_22 = Motor(Port.F, Direction.COUNTERCLOCKWISE)

async def turn_attachment(attach_motor, speed, degree):
    await wait(1)
    await multitask(attach_motor.run_angle(speed, degree, Stop.BRAKE), wait(1000), race=True)

async def configurre():
    await wait(1)
    drive_base.use_gyro(True)
    drive_base.reset()
    drive_base.settings(straight_speed=300)
    drive_base.settings(straight_acceleration=400)
    drive_base.settings(turn_rate=200)
    drive_base.settings(turn_acceleration=750)

async def main():
    await configurre()
    await drive_base.straight(-100, Stop.BRAKE)
    await drive_base.turn(60)
    await drive_base.straight(-180)
    await drive_base.turn(-50)
    await drive_base.straight(-80)
    await drive_base.turn(90)
    await drive_base.straight(-65)
    await turn_attachment(motor_22, 100, 130)
    await drive_base.straight(-55, Stop.BRAKE)
    await turn_attachment(motor_22, 500, -100)
    await drive_base.straight(130, Stop.BRAKE)
    await drive_base.turn(45)
    await turn_attachment(motor_22, 500, -150)
    await drive_base.straight(50)


run_task(main())