from pybricks.hubs import PrimeHub
from pybricks.parameters import Button, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
motor_22 = Motor(Port.F, Direction.COUNTERCLOCKWISE)
motor = Motor(Port.B, Direction.COUNTERCLOCKWISE)
motor_2 = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(motor, motor_2, 56, 130)

async def turn_attachment(attach_motor, speed, degree):
    await wait(1)
    await multitask(attach_motor.run_angle(speed, degree, Stop.BRAKE), wait(1000), race=True)

async def configure():
    await wait(1)
    drive_base.use_gyro(True)
    drive_base.reset()
    drive_base.settings(straight_speed=300)
    drive_base.settings(straight_acceleration=400)
    drive_base.settings(turn_rate=200)
    drive_base.settings(turn_acceleration=750)

async def main():
    await configure()
    await drive_base.straight(-420, Stop.BRAKE)
    await drive_base.turn(-57, Stop.BRAKE)
    await drive_base.straight(-75, Stop.BRAKE)
    await drive_base.straight(75, Stop.BRAKE)
    await drive_base.turn(-67, Stop.BRAKE)
    await drive_base.straight(-350, Stop.BRAKE)
    await drive_base.turn(65)
    await drive_base.straight(-75, Stop.COAST)
    while not any(prime_hub.buttons.pressed()):
        await wait(1)
    await drive_base.straight(-120, Stop.BRAKE)
    await turn_attachment(motor_22, 1500, 210)
    await drive_base.straight(175, Stop.BRAKE)
    await drive_base.turn(-75, Stop.COAST)
    await turn_attachment(motor_22, 1000, -30)
    while not any(prime_hub.buttons.pressed()):
        await wait(1)
    await drive_base.straight(190, Stop.BRAKE)
    await drive_base.turn(8, Stop.BRAKE)
    await drive_base.straight(-220, Stop.BRAKE)


run_task(main())